import { Card, GetProp, Layout, Upload, UploadFile, UploadProps } from 'antd';
import Meta from 'antd/es/card/Meta';
import { Content } from 'antd/es/layout/layout';
import Sider from 'antd/es/layout/Sider';
import { useState } from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';
import { useAppSelector } from '~/hooks/useStore';
import icons from '~/utils/icons';
import PATH from '~/utils/path';

const {
  CalendarOutlined,
  DeleteOutlined,
  FilePdfOutlined,
  SettingOutlined,
  ShopOutlined,
  UserOutlined,
  HomeOutlined,
} = icons;

type FileType = Parameters<GetProp<UploadProps, 'beforeUpload'>>[0];

const UserLayout = () => {
  const navigate = useNavigate();
  const { currentUser } = useAppSelector((state) => state.auth);
  const [fileList, setFileList] = useState<UploadFile[]>([
    {
      uid: '-1',
      name: 'image.png',
      status: 'done',
      url: 'data:image/jpeg;base64,',
    },
  ]);

  const onChange: UploadProps['onChange'] = ({ fileList: newFileList }) => {
    setFileList(newFileList);
  };

  const onPreview = async (file: UploadFile) => {
    let src = file.url as string;
    if (!src) {
      src = await new Promise((resolve) => {
        const reader = new FileReader();
        reader.readAsDataURL(file.originFileObj as FileType);
        reader.onload = () => resolve(reader.result as string);
      });
    }
    const image = new Image();
    image.src = src;
    const imgWindow = window.open(src);
    imgWindow?.document.write(image.outerHTML);
  };

  const coverImageUrl =
    fileList.length > 0 && fileList[0].url
      ? fileList[0].url
      : 'data:image/jpeg;base64,';

  return (
    <Layout className="min-h-screen">
      <Sider theme="light" width={400}>
        <Card
          cover={
            <img
              alt="example"
              src={coverImageUrl}
              style={{ height: 200, objectFit: 'cover', borderRadius: 0 }}
            />
          }
          actions={[
            <HomeOutlined key="home" onClick={() => navigate(PATH.ROOT)} />,
            <SettingOutlined key="setting" />,
            <DeleteOutlined key="delete" />,
          ]}
        >
          <Meta
            avatar={
              <Upload
                action="https://660d2bd96ddfa2943b33731c.mockapi.io/api/upload"
                listType="picture-card"
                fileList={fileList}
                onChange={onChange}
                onPreview={onPreview}
              >
                {fileList.length < 1 && <UserOutlined />}
              </Upload>
            }
            title={currentUser.fullName}
            description="This is the description"
          />
          <div className="flex flex-col gap-4 mt-3">
            <h3 className="font-bold">Hồ sơ</h3>
            <p className="cursor-pointer hover:text-gray-500 flex gap-2 items-center">
              <span>
                <UserOutlined />
              </span>
              <Link to={PATH.USER_PROFILE}>Cá nhân</Link>
            </p>
            <p className="cursor-pointer hover:text-gray-500 flex gap-2 items-center">
              <span>
                <CalendarOutlined />
              </span>
              <span>Công việc mong muốn</span>
            </p>
            <p className="cursor-pointer hover:text-gray-500 flex gap-2 items-center">
              <span>
                <FilePdfOutlined />
              </span>
              <span>CV</span>
            </p>
            <h3 className="font-bold">Công việc</h3>
            <p className="cursor-pointer hover:text-gray-500">
              <span>
                <ShopOutlined />
              </span>
              <span> Doanh nghiệp tiếp cận</span>
            </p>
          </div>
        </Card>
      </Sider>

      <Content className="p-6 bg-gray-100">
        <div className="bg-white p-4 rounded-lg shadow">
          <Outlet />
        </div>
      </Content>
    </Layout>
  );
};

export default UserLayout;
